var crypto = require('crypto');

var algorithm = 'aes256';
var secret = 'fobarbaz';

var val = '{"cookie":{"path":"/","httpOnly":true},"foo":"bar"}';

var cipher = crypto.createCipher(algorithm, secret);
val = cipher.update(val, 'utf8', 'binary');
val += cipher.final('binary');

console.log(Buffer(val).toString('base64'));
